export interface WhatsAppContact {
    id: string;
    /** The name of the contact as saved on your device. */
    savedName?: string;
    /** The name of the contact as set by the contact on WhatsApp. */
    displayName?: string;
    /** The verified name of the contact, if available. */
    verifiedDisplayName?: string;
    /**
     * The URL of the contact's profile picture. This can be one of the following:
     * - 'changed' if the profile picture has changed
     * - null if the contact has not set a profile picture (default profile picture)
     * - a URL pointing to the profile picture
     */
    profilePictureUrl?: string | null | 'changed';
    status?: string;
}
