'use strict';

const internals = {};

/**
 * Check if a given value is a promise-like object
 * @param {Object|Function} value - The value to check
 * @returns {Boolean} True if the value is a promise-like object, false otherwise
 */
const isPromiseLike = value => {
  if (value && typeof value === 'object' && typeof value.then === 'function') {
    return true;
  }
  return false;
};

module.exports = isPromiseLike;

