// Import statements
import setPrivateStaticField from "./classSetPrivateStaticField.js";
import checkPrivateStaticAccess from "./classCheckPrivateStaticAccess.js";
import checkPrivateStaticFieldDescriptor from "./classCheckPrivateStaticFieldDescriptor.js";

// Rename the function to follow conventions and better describe its purpose
export default function setClassStaticPrivateField(receiver, classConstructor, descriptor) {
  // Check if the receiver has private static access to the classConstructor
  checkPrivateStaticAccess(receiver, classConstructor);

  // Check if the descriptor is a valid private static field descriptor with a set method
  checkPrivateStaticFieldDescriptor(descriptor, "set");

  // Set the private static field value
  setPrivateStaticField(receiver, descriptor);
}
