import checkClassStaticPrivateAccess from "./classCheckPrivateStaticAccess.js";

/**
 * Retrieves a private static method from a class constructor.
 * @param {object} receiver - The object that the method belongs to.
 * @param {function} classConstructor - The class constructor.
 * @param {string} method - The name of the private static method.
 * @returns {function} The private static method.
 */
export default function getClassStaticPrivateMethod(receiver, classConstructor, method) {
  if (!checkClassStaticPrivateAccess(receiver, classConstructor)) return;
  return method;
}
