export default function skipFirstGeneratorNext(fn) {
  if (typeof fn !== 'function' || fn.constructor.name !== 'GeneratorFunction') {
    throw new Error('The input function must be a generator function');
  }

  return function () {
    const generator = fn.apply(this, arguments);

    if (generator.next().done) {
      throw new Error('The generator function does not yield any value');
    }

    return {
      next: (...params) => generator.next(...params),
      return: (...params) => generator.return(...params),
      throw: (...params) => generator.throw(...params),
    };
  };
}
