// assert.ts

/**
 * Asserts that the given condition is true. If it's not, an error with the given message is thrown.
 * @param condition - The condition to check.
 * @param message - The error message to display if the condition is not true.
 */
export function assert(condition: any, message?: string): asserts condition {
  if (!condition) {
    throw new Error(message || "Assertion failed");
  }
}
