'use strict';

const internals = {};

/**
 * Returns the keys of an object.
 * @param {Object} obj - The object to extract keys from.
 * @param {Object} options - Optional settings object.
 * @param {boolean} [options.symbols=true] - Whether to include symbol keys.
 * @returns {Array} An array of keys.
 */
exports.keys = function (obj, options = { symbols: true }) {
  return Reflect.ownKeys(obj).filter(key => options.symbols || typeof key !== 'symbol');
};
