import { DefError } from "./deferror.js";

// Create a custom error class for IllegalStateError
class IllegalStateError extends DefError {
  constructor(message) {
    super(message);
    this.name = "IllegalStateError";
  }
}

// Export the custom error class and the illegalState function
export { IllegalStateError };
export const illegalState = (message) => {
  throw new IllegalStateError(message || "Illegal state");
};
