// File identification magic number and format version.
const FONT_MAGIC = "flf2";
const FONT_VERSION = "a";

// File format specification.
// The first line of the file contains these values separated by spaces.
const FONT_FILE_FORMAT = `${FONT_MAGIC} ${FONT_VERSION} 
  6 5 20 15 12`;

// Font metadata.
const FONT_METADATA = `
Alphabet by Wendell Hicken 11/93 (whicken@parasoft.com)

Explanation of first line:
flf2 - "magic number" for file identification
a    - should always be \`a', for now
$    - the "hardblank" -- prints as a blank, but can't be smushed
6    - height of a character
5    - height of a character, not including descenders
20   - max line length (excluding comment lines) + a fudge factor
15   - default smushmode for this font (like "-m 15" on command line)
12   - number of comment lines
`;

// Font data.
const FONT_DATA = `
... (the rest of the provided font data)
`;

// Export the complete font data as a single string.
module.exports = `${FONT_FILE_FORMAT}\n${FONT_METADATA}${FONT_DATA}`;

